Logfile="output.txt"


usage=""$0" -u www.site.com -t 1 -o output.txt

where:
    -h  show this help text
    -u  url(www.site.com)
    -t  Site Language(1.php,2.asp,3.html,4.jsp,5.other)
    -o  Choose output name file(Default is output.txt)"

while getopts ":hu:t:o:" opt; do
  case "$opt" in
    h) echo "$usage"
       exit
         ;;
    u) surl=$OPTARG
	 ;;
    t) type=$OPTARG
         ;;
    o) Logfile=$OPTARG
         ;;
   \?) printf "illegal option: -%s\n" "$OPTARG"
       echo "$usage"
       exit 1
        ;;

  esac
done
shift $(( OPTIND - 1 ))

green='\e[1;32m'
NC='\e[0m' # No Color
red='\e[1;31m'





case "$type" in
"1")
    for i in "${php[@]}"
do
    status=$(curl -s --head -w %{http_code} $surl$i -o /dev/null)
	if [[ $status == 200 ]]
	then
            echo -e "${green}<Y>\t$surl$i${NC}"
	    echo $surl$i >> $Logfile
	else
	    echo -e "${red}<N>\t$surl$i${NC}"
	fi
done
exit 1
    ;;
"2")
    for i in "${asp[@]}"
do
